  }), mdx("h1", null, "\uB9DB"), mdx("p", null, "\uC778\uAC04\uC740 6\uAC00\uC9C0\uC758 \uB9DB\uC744 \uB290\uB084 \uC218 \uC788\uB2E4.\n\uADF8 \uC911 \uB2E8\uB9DB, \uC9E0\uB9DB, \uAC10\uCE60\uB9DB, \uB290\uB07C\uD55C \uB9DB\uC774 \uC778\uAC04\uC774 \uB9DB\uC788\uB2E4\uACE0 \uB290\uB07C\uB294 \uB9DB\uC774\uBA70, \uC2E0\uB9DB\uACFC \uC4F4\uB9DB\uC740 \uB9DB\uC774 \uC5C6\uB2E4\uACE0 \uB290\uB080\uB2E4."), mdx("h2", null, "\uB2E8\uB9DB"), mdx("p", null, "\uB2E8\uB9DB\uC740 \uC8FC\uB85C \uD0C4\uC218\uD654\uBB3C\uC5D0\uC11C \uB098\uB294 \uB9DB\uC774\uB2E4. \uC778\uAC04\uC758 \uBAB8\uC740 \uB2E8\uB9DB\uC744 \uB290\uB07C\uBA74 \uC0C8\uB85C\uC6B4 \uC5D0\uB108\uC9C0\uC6D0\uC774 \uB4E4\uC5B4\uC654\uB2E4\uACE0 \uC778\uC2DD\uD558\uB3C4\uB85D \uC9C4\uD654\uD574\uC654\uB2E4.\n\uC774\uC5D0 \uB530\uB77C \uBAB8\uC5D0\uC11C\uB294 \uB450\uB1CC\uC5D0 \uC790\uADF9\uC744 \uC90C\uC73C\uB85C\uC368 \uB354\uC6B1 \uD0C4\uC218\uD654\uBB3C\uC744 \uCC3E\uB3C4\uB85D \uC720\uB3C4\uD574 \uC0DD\uC874\uC5D0 \uC720\uB9AC\uD558\uB3C4\uB85D \uB9CC\uB4E0\uB2E4.\n\uC774 \uB54C \uC8FC\uB294 \uC790\uADF9\uC774 \uBC14\uB85C \uCF8C\uAC10\uC73C\uB85C, \uAC04\uB2E8\uD788 \uB9D0\uD574 \uC778\uAC04\uC740 \uB2E8 \uAC83\uC744 \uBA39\uC73C\uBA74 \uCF8C\uAC10\uC744 \uB290\uB080\uB2E4. "), mdx("h2", null, "\uC9E0\uB9DB"), mdx("p", null, "\uC9E0\uB9DB\uC740 \uC8FC\uB85C \uC5FC\uD654\uB098\uD2B8\uB968(\uC18C\uAE08)\uC5D0\uC11C \uB098\uB294 \uB9DB\uC774\uB2E4.\n\uC18C\uAE08\uC744 \uC12D\uCDE8\uD568\uC73C\uB85C\uC368 \uBAB8\uC5D0 \uB4E4\uC5B4\uC628 \uB098\uD2B8\uB968\uC740 \uC0DD\uC874\uC5D0 \uD544\uC218\uC801\uC778 \uC5ED\uD560\uC744 \uD558\uAE30 \uB54C\uBB38\uC5D0 \uC774 \uB9DB\uB3C4 \uB9DB\uC788\uB2E4\uACE0 \uB290\uB07C\uB3C4\uB85D \uC9C4\uD654\uD588\uB2E4.\n\uADF8\uB7EC\uB098 \uACFC\uD55C \uC5FC\uBD84\uC740 \uC624\uD788\uB824 \uBAB8\uC5D0 \uD574\uB85C\uC6CC\uC11C \uB108\uBB34 \uC9DC\uBA74 \uC6B0\uB9AC \uBAB8\uC740 \uAC70\uBD80\uBC18\uC751\uC744 \uC77C\uC73C\uD0A4\uBA70 \uB2E4\uC2DC \uB9DB\uC774 \uC5C6\uB2E4\uACE0 \uB290\uB080\uB2E4."), mdx("h2", null, "\uAC10\uCE60\uB9DB"), mdx("p", null, "Savory\uB77C\uACE0 \uD558\uBA70, \uB2E8\uBC31\uC9C8\uC5D0\uC11C \uB098\uB294 \uB9DB\uC774\uB2E4. \uC774\uB294 \uC721\uB958 \uC678\uC5D0\uB3C4 \uCE58\uC988\uB098 \uAC04\uC7A5, \uD1A0\uB9C8\uD1A0, \uACE1\uBB3C, \uCF69\uB958\uC5D0\uC11C\uB3C4 \uB290\uB084 \uC218 \uC788\uB2E4.\n\uB2E8\uBC31\uC9C8\uC740 \uC0DD\uC874\uC5D0 \uD544\uC218\uC801\uC778 \uADFC\uC721\uC870\uC9C1\uC744 \uB9CC\uB4DC\uB294 \uB370 \uD544\uC694\uD558\uBBC0\uB85C, \uC778\uAC04\uC758 \uBAB8\uC740 \uAC10\uCE60\uB9DB\uC744 \uB290\uB07C\uBA74 \uB354\uC6B1 \uC2DD\uC695\uC774 \uB2F9\uAE30\uB3C4\uB85D \uC9C4\uD654\uD588\uB2E4.\n\uC774 \uC2DD\uC695\uC744 \uB2F9\uAE30\uB294 \uD2B9\uC131 \uB54C\uBB38\uC5D0 \uD754\uD788 \uD5A5\uBBF8\uC99D\uC9C4\uC81C\uB77C\uACE0 \uD558\uB294 MSG\uB294 \uAC15\uD55C \uAC10\uCE60\uB9DB\uC774 \uB290\uAEF4\uC9C0\uB3C4\uB85D \uC81C\uC870\uB41C\uB2E4."), mdx("h2", null, "\uB290\uB07C\uD55C \uB9DB"), mdx("p", null, "Oleogustus\uB77C\uACE0 \uD558\uBA70, \uAE30\uB984\uC774 \uB0B4\uB294 \uACE0\uC18C\uD55C \uB9DB\uC774\uB2E4. \uADF8\uB7EC\uB098 \uC774 \uB9DB\uC740 \uB2E4\uB978 \uB9DB\uB4E4\uACFC\uB294 \uB2E4\uB978 \uBA87\uAC00\uC9C0 \uC7AC\uBC0C\uB294 \uD2B9\uC131\uC774 \uC788\uB2E4."), mdx("ul", null, mdx("li", {
    parentName: "ul"
  }, "\uC0AC\uB78C\uC5D0 \uB530\uB77C \uC774 \uB9DB\uC744 \uAC15\uD558\uAC8C \uB290\uB07C\uB294 \uC0AC\uB78C\uC774 \uC788\uACE0, \uADF8\uB807\uC9C0 \uC54A\uC740 \uC0AC\uB78C\uC774 \uC788\uB2E4."), mdx("li", {
    parentName: "ul"
  }, "\uC774 \uB9DB\uC774 \uD63C\uC790 \uB3D9\uB5A8\uC5B4\uC838 \uC788\uC73C\uBA74 \uB9DB\uC774 \uC5C6\uACE0, \uC790\uADF9\uC801\uC774\uBA70 \uBD88\uCF8C\uD558\uB2E4\uACE0 \uB290\uAEF4\uC9C4\uB2E4."), mdx("li", {
    parentName: "ul"
  }, "\uAE08\uBC29 \uC775\uC219\uD574\uC838 \uD600\uC758 \uAC10\uAC01\uC744 \uB454\uD558\uAC8C \uB9CC\uB4E0\uB2E4.")), mdx("p", null, "\uC989, \uB2E4\uB978 \uB9DB\uB4E4\uACFC \uAC19\uC774 \uC5B4\uC6B0\uB7EC\uC84C\uC744 \uB54C\uC5D0\uC57C \uC9C4\uAC00\uB97C \uBC1C\uD718\uD558\uB294 \uB9DB\uC774\uB2E4."), mdx("h2", null, "\uC2E0\uB9DB"), mdx("p", null, "\uC2E0\uB9DB\uC740 \uC8FC\uB85C \uC0C1\uD55C \uC74C\uC2DD\uC5D0\uC11C \uB9CE\uC774 \uB098\uB294 \uB9DB\uC774\uAE30 \uB54C\uBB38\uC5D0, \uC778\uAC04\uC740 \uC774 \uB9DB\uC5D0 \uB9E4\uB825\uC744 \uC798 \uB290\uB07C\uC9C0 \uBABB\uD55C\uB2E4.\n\uADF8\uB7EC\uB098 \uBE44\uD0C0\uBBFC C\uAC19\uC740 \uACBD\uC6B0 \uC2E0 \uB9DB\uC774 \uB098\uB294 \uC74C\uC2DD\uC5D0\uC11C \uC12D\uCDE8\uD560 \uC218 \uC788\uAE30 \uB54C\uBB38\uC5D0 \uC544\uC8FC \uADF9\uC18C\uB7C9\uC758 \uC2E0\uB9DB\uC740 \uC624\uD788\uB824 \uC2DD\uC695\uC744 \uB3CB\uAD6C\uAE30\uB3C4 \uD55C\uB2E4."), mdx("h2", null, "\uC4F4\uB9DB"), mdx("p", null, "\uC4F4\uB9DB\uC740 \uBD88\uCF8C\uAC10\uC744 \uC8FC\uB294 \uB9DB\uC774\uB2E4. \uB300\uBD80\uBD84\uC758 \uB3C5\uC131 \uBB3C\uC9C8\uC5D0\uB294 \uC4F4 \uB9DB\uC774 \uB098\uAE30 \uB54C\uBB38\uC5D0 \uC778\uAC04\uC758 \uBAB8\uC740 \uC4F4\uB9DB\uC744 \uAE30\uD53C\uD558\uB3C4\uB85D \uC9C4\uD654\uD588\uB2E4.\n\uB2E4\uB978 \uB9DB\uB4E4\uC5D0 \uBE44\uD574 \uB9DB\uC774 \uB290\uAEF4\uC9C8 \uB54C\uAE4C\uC9C0 \uAC78\uB9AC\uB294 \uC2DC\uAC04\uC774 \uAE38\uACE0, \uB610 \uB9DB\uC774 \uC624\uB798 \uB0A8\uC544 \uC798 \uAC00\uC2DC\uC9C0 \uC54A\uB294\uB2E4.\n\uC774 \uC4F4 \uB9DB\uC758 \uB4B7\uB9DB\uC744 \uAC00\uC2DC\uAC8C \uD558\uB294 \uB370\uB294 \uB2E8\uB9DB\uC774 \uAC00\uC7A5 \uC88B\uB2E4."), mdx("p", null, "\uADF8\uB7EC\uB098 \uC2E0\uB9DB\uACFC \uB9C8\uCC2C\uAC00\uC9C0\uB85C \uADF9\uC18C\uB7C9\uC758 \uC4F4\uB9DB\uC740 \uC74C\uC2DD\uC758 \uB9DB\uC744 \uB0B4\uB294 \uB370 \uB3C4\uC6C0\uC774 \uB41C\uB2E4.\n\uC774\uB294 \uC2E0\uB9DB\uACFC\uB294 \uB2E4\uB974\uAC8C \uBAB8\uC774 \uB9DB\uC788\uB2E4\uACE0 \uB290\uAEF4\uC11C \uB9DB\uC788\uB294 \uAC83\uC774 \uC544\uB2C8\uB77C \uB2E4\uB978 \uB9DB\uC744 \uB3CB\uBCF4\uC774\uAC8C \uD568\uC73C\uB85C\uC368 \uB9DB\uC788\uB294 \uAC83\uC774\uB2E4.\n\uCEE4\uD53C\uB098 \uCC28\uAC00 \uBC14\uB85C \uADF8\uB7F0 \uC6A9\uB3C4\uB85C \uC4F0\uC778\uB2E4."), mdx("p", null, "\uB610 10\uB3C4 \uC815\uB3C4 \uCC28\uAC00\uC6B4 \uC74C\uC2DD\uC5D0\uC11C \uAC00\uC7A5 \uADF8 \uB9DB\uC774 \uAC15\uD558\uB2E4. \uC2DD\uC5B4\uBC84\uB9B0 \uD55C\uC57D\uC744 \uB5A0\uC62C\uB9AC\uBA74 \uC774\uD574\uAC00 \uBE60\uB974\uB2E4."), mdx("h2", null, "\uADF8 \uC678 \uB2E4\uB978 \uB9DB"), mdx("h3", null, "\uB9E4\uC6B4 \uB9DB"), mdx("p", null, "\uB9E4\uC6B4 \uB9DB\uC740 \uD600\uC758 \uBBF8\uB8B0\uB85C \uB290\uAEF4\uC9C0\uB294 \uB9DB\uC774 \uC544\uB2CC, \uB2E8\uC21C\uD55C \uC785\uC548\uC758 \uD1B5\uC99D\uC774\uB2E4. \uADF8\uB7EC\uB098 \uD1B5\uC99D\uC740 \uB54C\uB54C\uB85C \uCF8C\uAC10\uC744 \uAC00\uC838\uB2E4 \uC8FC\uAE30\uB3C4 \uD55C\uB2E4."), mdx("h3", null, "\uC0AC\uACFC \uB9DB, \uD3EC\uB3C4 \uB9DB \uB4F1 \uACFC\uC77C \uB9DB"), mdx("p", null, "\uC5ED\uC2DC \uBBF8\uB8B0\uB85C \uB290\uAEF4\uC9C0\uB294 \uB9DB\uC774 \uC544\uB2CC, \uD6C4\uAC01\uC73C\uB85C \uB290\uB07C\uB294 \uB9DB\uC774\uB2E4."));
}
